const Index = ({ posts }) => {
  return (
    <div>
      <h1>Index Page!!!</h1>
      <ul>
        {posts.map(post => (
          <li key={post.id}>
            <Link href={`/post?id=${post.id}`} as>
              <a>{post.title}</a>
            </Link>
          </li>
        ))}
      </ul>
    </div>
  )
}

Index.getInitialProps = async () => {
  const res = await axios.get('https://jsonplaceholder.typicode.com/posts')
  const { data } = res

  return { posts: data }
}

export default Index
