const Index = ({ posts }) => {
  return (
    <div>
      <h1>Index Page!!!</h1>
      {posts.map()}
    </div>
  )
}

Index.getInitialProps = async () => {
  const res = await axios.get('https://jsonplaceholder.typicode.com/posts')
  const { data } = res

  return { posts: data }
}

export default Index
